fun main() {
    day6Part2()
}

fun day6Part1() {
    val input = ""
    var windowStart = 0
    var set = mutableSetOf<Char>()
    run breaking@{
        for (windowEnd in 0..input.length) {
            if (set.contains(input[windowEnd])) {
                while (set.contains(input[windowEnd])) {
                    set.remove(input[windowStart++])
                }
            }
            set.add(input[windowEnd])
            if (set.size == 4) {
                print("ans is ${windowEnd + 1}")
                return@breaking
            }
        }
    }
}

